$=jQuery;$(document).ready(function(){$('#place_order').unbind().click(function(e){e.preventDefault();if(($('#billing_first_name').val()=='')||($('#billing_last_name').val()=='')||($('#billing_email').val()=='')){alert('Please make sure you have filled out first name, last name, and email to receive your order.');}else if(($('input[name="ss_wc_mailchimp_opt_in"]:checked').length<1)&&(localized_sexy_config.total==0)){let result=confirm("Please consider allowing me to email you my new new music and show announcements. It's the only way I can continue giving away free music! Please click OK to accept!");if(result){$('input[name="ss_wc_mailchimp_opt_in"]').prop('checked',true);$('form[name="checkout"]').submit();}else{$('form[name="checkout"]').submit();}}else{$('form[name="checkout"]').submit();}});$('#powered').css('display','none');$('.home-subscribe-widget').hide();if(!readCookie('disableLandingPagePopUpNew')){$('.home-subscribe-widget').fadeIn('fast');}
$('.filter-drop-spacer').remove();$('.close').unbind().click(function(e){e.preventDefault();var today=new Date();var nextweek=new Date(today.getFullYear(),today.getMonth(),today.getDate()+7);document.cookie='disableLandingPagePopUpNew=1; '+'expires='+nextweek+';'+'path=/';$('.home-subscribe-widget').fadeOut('fast');});if($(window).width()<768){}else{$('.menu-item a').unbind().click(function(e){if($(this).attr('href')=='#'){e.preventDefault();window.location=$(this).parent().find('ul li a').first().attr('href');}});}});if($('p').hasClass('confirmed')){$('.instructions').hide();}
function readCookie(name){var nameEQ=name+"=";var ca=document.cookie.split(';');for(var i=0;i<ca.length;i++){var c=ca[i];while(c.charAt(0)==' ')c=c.substring(1,c.length);if(c.indexOf(nameEQ)==0)return c.substring(nameEQ.length,c.length);}
return null;};